import React, { useState } from 'react';

const UserProfile = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [profilePicture, setProfilePicture] = useState(null);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleProfilePictureChange = (e) => {
    const selectedPicture = e.target.files[0];
    setProfilePicture(selectedPicture);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create a FormData object to send the data to the server
    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    if (profilePicture) {
      formData.append('profilePicture', profilePicture);
    }

    try {
      // Make an API request to update the user's profile
      const response = await fetch('http://localhost:8000/api/updateProfile', {
        method: 'POST',
        body: formData,
      });

      if (response.status === 200) {
        // Profile updated successfully
        console.log('Profile updated successfully');
      } else {
        // Handle error
        console.error('Error updating profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div>
      <h2>User Profile</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={handleNameChange} />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={handleDescriptionChange} />
        </div>
        <div>
          <label>Profile Picture:</label>
          <input type="file" accept="image/*" onChange={handleProfilePictureChange} />
        </div>
        <div>
          <button type="submit">Update Profile</button>
        </div>
      </form>
      <div>
        <h3>Current Profile</h3>
        <div>
          {profilePicture && (
            <img src={URL.createObjectURL(profilePicture)} alt="Profile" width="100" />
          )}
        </div>
        <div>
          <p>Name: {name}</p>
          <p>Description: {description}</p>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
