import React, { useState } from 'react';
import styles from '../Blog/styles.module.css';
import axios from 'axios';

const BlogForm = ({ onSubmit, className }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [image, setImage] = useState(null);

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleContentChange = (e) => {
    setContent(e.target.value);
  };

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setImage(selectedImage);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !content) {
      // Handle validation or show an error message
      return;
    }

    try {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('content', content);
      formData.append('image', image); // If you have an image to upload

      const response = await axios.post('http://localhost:8080/api/blog', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.status === 201) {
        // Blog post created successfully, handle success or redirection
        console.log('Blog post created successfully');
        // You can redirect the user to a success page or perform any other action here
      }
    } catch (error) {
      // Handle error, if any
      console.error('Error creating blog post:', error);
      // You can show an error message to the user or perform error handling as needed
    }
  };

  return (
    <form className={`${styles.blog_form} ${className}`} onSubmit={handleSubmit}>
      <h2 className={styles.form_title}>Create a New Blog</h2>
      <input
        type="text"
        placeholder="Title"
        name="title"
        value={title}
        onChange={handleTitleChange}
        className={`${styles.input} ${styles.input_wide}`}
      />
      <textarea
        placeholder="Content"
        name="content"
        value={content}
        onChange={handleContentChange}
        className={`${styles.input} ${styles.input_wide} ${styles.textarea}`}
      ></textarea>
      <label htmlFor="image" className={styles.image_label}>
        Choose an Image:
        <input
          type="file"
          id="image"
          accept="image/*"
          onChange={handleImageChange}
          className={styles.file_input}
        />
      </label>
      <button type="submit" className={`${styles.green_btn} ${styles.btn}`}>
        Post
      </button>
    </form>
  );
};

export default BlogForm;
