import React, { useState } from 'react';
import styles from './styles.module.css';

const UserProfile = ({ className }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [profilePicture, setProfilePicture] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleProfilePictureChange = (e) => {
    const selectedPicture = e.target.files[0];
    setProfilePicture(selectedPicture);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    if (profilePicture) {
      formData.append('profilePicture', profilePicture);
    }

    try {
      const response = await fetch('http://localhost:8000/api/userProfile', {
        method: 'POST',
        body: formData,
      });

      if (response.status === 200) {
        console.log('Profile updated successfully');
      } else {
        setError('Error updating profile');
      }
    } catch (error) {
      setError('Error updating profile: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`${styles.profileContainer} ${className}`}>
      <h2>User Profile</h2>
      <form onSubmit={handleSubmit} className={styles.profileForm}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={handleNameChange}
            className={styles.profileInput}
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
            value={description}
            onChange={handleDescriptionChange}
            className={styles.profileInput}
          />
        </div>
        <div>
          <label>Profile Picture:</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleProfilePictureChange}
          />
        </div>
        <div>
          {loading ? (
            <p>Loading...</p>
          ) : (
            <button type="submit" className={styles.profileButton}>
              Update Profile
            </button>
          )}
        </div>
        {error && <p className={styles.error}>{error}</p>}
      </form>
      <div className={styles.profilePreview}>
        <h3>Current Profile</h3>
        <div className={styles.profilePicture}>
          {profilePicture && (
            <img
              src={URL.createObjectURL(profilePicture)}
              alt="Profile"
              width="100"
            />
          )}
        </div>
        <div>
          <p>Name: {name}</p>
          <p>Description: {description}</p>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
