import React, { useState } from 'react';
import styles from './styles.module.css';

const UserProfile = ({ className }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [profilePicture, setProfilePicture] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleProfilePictureChange = (e) => {
    const selectedPicture = e.target.files[0];
    setProfilePicture(selectedPicture);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    if (profilePicture) {
      formData.append('profilePicture', profilePicture);
    }

    try {
      const response = await fetch('http://localhost:8000/api/updateProfile', {
        method: 'POST',
        body: formData,
      });

      if (response.status === 200) {
        console.log('Profile updated successfully');
      } else {
        setError('Error updating profile');
      }
    } catch (error) {
      setError('Error updating profile: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`${styles.profileContainer} ${className}`}>
      <h2>User Profile</h2>
      <form onSubmit={handleSubmit} className={styles.profileForm}>
        {/* ... Form fields ... */}
        <div>
          {loading ? (
            <p>Loading...</p>
          ) : (
            <button type="submit" className={styles.profileButton}>
              Update Profile
            </button>
          )}
        </div>
        {error && <p className={styles.error}>{error}</p>}
      </form>
      {/* ... Current profile display ... */}
    </div>
  );
};

export default UserProfile;
